package com.jararat.listadinamicaheroes

import android.content.Context
import android.content.DialogInterface
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.LayoutInflater
import android.widget.EditText
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.jararat.listadinamicaheroes.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity(), OnClickListener {

    private lateinit var heroeAdapter: HeroeAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager
    private lateinit var viewBinding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        viewBinding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(viewBinding.root)

        val preferences = getPreferences(Context.MODE_PRIVATE)
        val isFirstTime = preferences.getBoolean(getString(R.string.spFirstTime),true)
        var userName =""
        if(isFirstTime){
            val dialogView =  layoutInflater.inflate(R.layout.dialog_register,null)
            MaterialAlertDialogBuilder(this)
                .setView(dialogView)
                .setPositiveButton(R.string.confirm_dialog) { dialogInterface, i_ ->
                    val input = dialogView.findViewById<EditText>(R.id.etUserName)
                    userName =input.text.toString()

                    if (userName.isNullOrEmpty()){
                        //Toast.makeText(this, "Debe Ingresar un Nombre", Toast.LENGTH_SHORT) .show()
                        input.setError("Debe ingresar un Nombre")
                        return@setPositiveButton
                    }


                    with(preferences.edit()){
                        putBoolean(getString(R.string.spFirstTime),false)
                        putString(getString(R.string.spUserName), userName)
                        //.apply()
                            .commit()
                    }

                    ShowMessageWelcome(userName)
                }
                .show()

        }else{
            userName = preferences.getString(getString(R.string.spUserName),getString(R.string.userDefault)).toString()
            ShowMessageWelcome(userName)
        }

        heroeAdapter = HeroeAdapter(getHeroes(), this)
        linearLayoutManager = LinearLayoutManager(this)

        viewBinding.rvListaHeroes.apply {
            layoutManager =  linearLayoutManager
            adapter =  heroeAdapter
        }
    }

    private  fun ShowMessageWelcome(UserName: String ){
        Toast.makeText(this, "Bienvenido $UserName", Toast.LENGTH_SHORT)
            .show()
    }


    private  fun getHeroes():MutableList<Heroe>{
        val heroes = mutableListOf<Heroe>()

        val ironMan =  Heroe("Iron Man", "Tony Start","data:image/jpeg;base64,")
        heroes.add(ironMan)

        val hulk =  Heroe("Hulk", "Banner","data:image/jpeg;base64,")
        heroes.add(hulk)

        val blackWidow =  Heroe("Black Widow", "Natasha Romanoff", "data:image/jpeg;base64," )
        heroes.add(blackWidow)

        val capAmerica =  Heroe("Capitan America", "Steven Rogers", "data:image/jpeg;base64,")
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(hulk)
        heroes.add(capAmerica)
        heroes.add(ironMan)

        return heroes
    }

    override fun onClick(heroe: Heroe) {
        Toast.makeText(this,heroe.getFullName(),Toast.LENGTH_SHORT).show()
    }
}